const CoffeeChatHero = () => {
  const [scrollY, setScrollY] = useState(0);
  const [activeStep, setActiveStep] = useState(0);
  const [location, setLocation] = useState('');

  useEffect(() => {
    const handleScroll = () => {
      setScrollY(window.scrollY);
      const scrollProgress = window.scrollY / (document.documentElement.scrollHeight - window.innerHeight);
      if (scrollProgress < 0.25) setActiveStep(0);
      else if (scrollProgress < 0.5) setActiveStep(1);
      else if (scrollProgress < 0.75) setActiveStep(2);
      else setActiveStep(3);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const stories = [
    {
      name: "Sarah & Mike",
      story: "Met over lattes, now business partners",
      image: "https://picsum.photos/id/64/400/400"
    },
    {
      name: "James & Emma",
      story: "Coffee chat turned into a lifelong friendship",
      image: "https://picsum.photos/id/65/400/400"
    },
    {
      name: "Alex & Jordan",
      story: "Found their co-founder at a local cafe",
      image: "https://picsum.photos/id/91/400/400"
    }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-b from-amber-50 via-orange-50 to-amber-100">
      {/* Hero Section with Parallax */}
      <div className="relative min-h-screen flex items-center justify-center overflow-hidden">
        <div 
          className="absolute inset-0 z-0"
          style={{ transform: `translateY(${scrollY * 0.5}px)` }}
        >
          <img 
            src="https://picsum.photos/id/30/1920/1080" 
            alt="Coffee shop atmosphere" 
            className="w-full h-full object-cover opacity-20"
          />
        </div>

        <div className="relative z-10 max-w-5xl mx-auto px-6 py-20 text-center">
          <h1 
            className="text-7xl font-bold text-amber-900 mb-6 transition-all duration-1000"
            style={{ 
              opacity: 1 - scrollY / 500,
              transform: `translateY(${scrollY * 0.2}px)`
            }}
          >{{HERO_TITLE}}</h1>

          <p 
            className="text-2xl text-amber-800 mb-12 max-w-3xl mx-auto"
            style={{ 
              opacity: 1 - scrollY / 400,
              transform: `translateY(${scrollY * 0.3}px)`
            }}
          >{{DESCRIPTION}}</p>

          <div className="flex flex-col sm:flex-row gap-4 justify-center items-center mb-12">
            <input
              type="text"
              placeholder="Enter your city..."
              value={location}
              onChange={(e) => setLocation(e.target.value)}
              className="px-6 py-4 rounded-full text-lg w-full sm:w-80 border-2 border-amber-300 focus:border-orange-500 focus:outline-none shadow-lg"
            />
            <button className="px-10 py-4 bg-gradient-to-r from-orange-500 to-amber-600 text-white rounded-full text-lg font-semibold hover:shadow-2xl transform hover:scale-105 transition-all duration-300 w-full sm:w-auto">{{CTA_PRIMARY}}</button>
          </div>

          <div className="flex gap-8 justify-center text-amber-900">
            <div>
              <div className="text-4xl font-bold">12,847</div>
              <div className="text-sm">Connections Made</div>
            </div>
            <div>
              <div className="text-4xl font-bold">2,341</div>
              <div className="text-sm">Cities</div>
            </div>
            <div>
              <div className="text-4xl font-bold">98%</div>
              <div className="text-sm">Would Meet Again</div>
            </div>
          </div>
        </div>

        <div className="absolute bottom-10 left-1/2 transform -translate-x-1/2 animate-bounce">
          <div className="text-amber-600 text-sm mb-2">Scroll to explore</div>
          <svg className="w-6 h-6 mx-auto text-amber-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 14l-7 7m0 0l-7-7m7 7V3" />
          </svg>
        </div>
      </div>

      {/* Story Section - Scroll Based Animation */}
      <div className="min-h-screen py-20 px-6">
        <div className="max-w-6xl mx-auto">
          <h2 className="text-5xl font-bold text-center text-amber-900 mb-16">{{FEATURE_TITLE}}</h2>

          <div className="grid md:grid-cols-3 gap-8 mb-20">
            {stories.map((story, index) => (
              <div
                key={index}
                className="bg-white rounded-3xl overflow-hidden shadow-xl transform transition-all duration-700 hover:scale-105"
                style={{
                  opacity: scrollY > 300 ? 1 : 0,
                  transform: `translateY(${scrollY > 300 ? 0 : 50}px)`,
                  transitionDelay: `${index * 150}ms`
                }}
              >
                <img 
                  src={story.image} 
                  alt={story.name}
                  className="w-full h-64 object-cover"
                />
                <div className="p-6">
                  <h3 className="text-2xl font-bold text-amber-900 mb-2">{{FEATURE_1_TITLE}}</h3>
                  <p className="text-amber-700 text-lg">{story.story}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* How It Works - Interactive Steps */}
      <div className="min-h-screen py-20 px-6 bg-gradient-to-b from-amber-100 to-orange-100">
        <div className="max-w-6xl mx-auto">
          <h2 className="text-5xl font-bold text-center text-amber-900 mb-20">{{FEATURE_2_TITLE}}</h2>

          <div className="grid md:grid-cols-2 gap-12 items-center mb-12">
            <div className="space-y-8">
              {[
                { title: "Create Your Profile", desc: "Share your interests, what you're working on, and what kind of conversations you're looking for" },
                { title: "Browse Local Coffee Lovers", desc: "Discover people nearby who share your passions and are ready for meaningful conversations" },
                { title: "Send a Coffee Request", desc: "Pick a cozy cafe, suggest a time, and start the conversation" },
                { title: "Meet & Connect", desc: "Show up, be yourself, and build relationships that matter" }
              ].map((step, index) => (
                <div
                  key={index}
                  className={`p-6 rounded-2xl transition-all duration-500 cursor-pointer ${
                    activeStep === index 
                      ? 'bg-gradient-to-r from-orange-500 to-amber-600 text-white shadow-2xl scale-105' 
                      : 'bg-white text-amber-900 shadow-lg hover:shadow-xl'
                  }`}
                  onClick={() => setActiveStep(index)}
                >
                  <div className="flex items-center gap-4 mb-3">
                    <div className={`w-12 h-12 rounded-full flex items-center justify-center text-xl font-bold ${
                      activeStep === index ? 'bg-white text-orange-600' : 'bg-orange-100 text-orange-600'
                    }`}>
                      {index + 1}
                    </div>
                    <h3 className="text-2xl font-bold">{{FEATURE_3_TITLE}}</h3>
                  </div>
                  <p className={`ml-16 text-lg ${activeStep === index ? 'text-orange-50' : 'text-amber-700'}`}>{{DESCRIPTION_1}}</p>
                </div>
              ))}
            </div>

            <div className="relative">
              <img 
                src="https://picsum.photos/id/431/800/1000" 
                alt="Coffee meeting" 
                className="rounded-3xl shadow-2xl w-full object-cover"
              />
              <div className="absolute inset-0 bg-gradient-to-t from-amber-900/50 to-transparent rounded-3xl"></div>
            </div>
          </div>
        </div>
      </div>

      {/* Final CTA */}
      <div className="py-20 px-6 bg-gradient-to-r from-orange-600 to-amber-700">
        <div className="max-w-4xl mx-auto text-center">
          <h2 className="text-5xl font-bold text-white mb-6">{{FEATURE_4_TITLE}}</h2>
          <p className="text-2xl text-orange-100 mb-10">{{DESCRIPTION_2}}</p>
          <button className="px-12 py-5 bg-white text-orange-600 rounded-full text-xl font-bold hover:shadow-2xl transform hover:scale-110 transition-all duration-300">
            Start Connecting Today
          </button>
        </div>
      </div>
    </div>
  );
};
